WHERE complaint_no = #{complaintNo}

mapper.select:
SELECT complaint_no AS complaintNo,id, complaint_title AS complaintTitle, complaint_content AS complaintContent, complaint_organization AS complaintOrganization, complaint_organization_detail AS complaintOrganizationDetail, complaint_date AS complaintDate, complaint_count AS complaintCount, complaint_status AS complaintStatus FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM complaint WHERE complaint_title like '%${complaintTitle}%' AND complaint_organization like '%${complaintOrganization}%' AND complaint_organization_detail like '%${complaintOrganizationDetail}%'
AND complaint_date >= #{complaintSdate} AND complaint_date-1 <= #{complaintEdate}
ORDER BY complaint_no DESC) A ) WHERE RN BETWEEN #{start} AND #{end} AND complaint_title like '%${complaintTitle}%' AND complaint_organization like '%${complaintOrganization}%' AND complaint_organization_detail like '%${complaintOrganizationDetail}%'
AND complaint_date >= #{complaintSdate} AND complaint_date-1 <= #{complaintEdate}

SELECT nvl(count(*),0) FROM suggestion
WHERE suggestion_title like '%${suggestionKeyword}%'


AND suggestion_date >= #{suggestionSdate} AND suggestion_date-1 <= #{suggestionEdate}

SELECT suggestion_no AS suggestionNo,id, suggestion_title AS suggestionTitle, suggestion_problem AS suggestionProblem, suggestion_plan AS suggestionPlan, suggestion_effect AS suggestionEffect, suggestion_date AS suggestionDate, suggestion_count AS suggestionCount, suggestion_status AS suggestionStatus FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM suggestion WHERE suggestion_title like '%${suggestionTitle}%'
AND suggestion_date >= #{suggestionSdate} AND suggestion_date-1 <= #{suggestionEdate}
ORDER BY suggestion_no DESC) A ) WHERE RN BETWEEN #{start} AND #{end} AND suggestion_title like '%${suggestionTitle}%'
AND suggestion_date >= #{suggestionSdate} AND suggestion_date-1 <= #{suggestionEdate}

mapper.update:
UPDATE suggestion SET
suggestion_status = 1
WHERE suggestion_no = #{suggestionNo}

UPDATE suggestion SET
suggestion_status = 2
WHERE suggestion_no = #{suggestionNo}
