mapper.select:
SELECT id, name, email, tel, birthday, reg_date AS regDate, wdr_date AS wdrDate FROM (
	SELECT ROWNUM RN, A.* 
		FROM (
				SELECT * 
				FROM member
				) A
		)
WHERE RN BETWEEN #{start} AND #{end}

SELECT id, name, email, tel, birthday, reg_date AS regDate, wdr_date AS wdrDate FROM (
	SELECT ROWNUM RN, A.* 
		FROM (
				SELECT * 
				FROM member
				WHERE wdr_date IS NOT NULL
				) A
		)
WHERE RN BETWEEN #{start} AND #{end}
